    &#39;{esc}&#39;        =&gt; &#39;[|]e&#39;,
    &#39;{hex}&#39;        =&gt; &#39;[|]x&#39;
],

//-------------------------------------------------------------------------------------
// Setting Chars
//-------------------------------------------------------------------------------------
//
// Genel Kullanımı: Düzenli ifadelerde oluşturulan desen sonuna konulan karakterlerle     
// ilgili aşağıdaki değişiklikler yapılmıştır                                             
//
//-------------------------------------------------------------------------------------
&#39;settingChars&#39; =&gt;
[
    &#39;{insens}&#39;    =&gt; &#39;i&#39;,
    &#39;{generic}&#39;   =&gt; &#39;g&#39;,
    &#39;{each}&#39;      =&gt; &#39;s&#39;,
    &#39;{multiline}&#39; =&gt; &#39;m&#39;,
    &#39;{inspace}&#39;   =&gt; &#39;x&#39;
],

//-------------------------------------------------------------------------------------
