    //-------------------------------------------------------------------------------------
    &#39;driverSettings&#39; =&gt;
    [
        //-------------------------------------------------------------------------------------
        // Memcache Connection Settings
        //-------------------------------------------------------------------------------------
        //
        // Memcache sürücüsü için gerekli olan bağlantı ayarları yer alır.
        //
